let fill = '#32cd79' // 填充色

/// 1. 头
const head = new Path({
    path: headPath,
    fill,
    around: 'center',
    cursor: 'pointer', // 增加按钮交互效果
    hoverStyle: { scale: 1.2 },
    pressStyle: { scale: 3, transition: 0.5 }
})
