// #心花怒放
import { Leafer, Box, Group, Path, Text, Rect, IGradientPaint } from 'leafer-game'

const leafer = new Leafer({ view: window, fill: '#111' })


// 第一步：准备愿望数据

let score = 0 // 心花怒放累计值
const bloomScore = 2000 // 达到这个值完成一次心花怒放
const sendInterval = 500 // 每多少毫秒随机发送一次愿望卡片

const wishList = [ // 愿望数据
    { id: 1, name: '愿望1', value: 100 },
    { id: 2, name: '愿望2', value: 300 },
    { id: 3, name: '愿望3', value: 200 },
    { id: 4, name: '愿望4', value: 200 },
    { id: 5, name: '愿望5', value: 100 },
    { id: 6, name: '愿望6', value: 300 },
    { id: 7, name: '愿望7', value: 200 }
]

const width = window.innerWidth, height = window.innerHeight // 界面宽高

const wishTracks = Math.floor((width - 30) / 90) // 发放愿望卡片的轨道数量


// 第二步：构建页面UI

// 开始游戏按钮

const color: IGradientPaint = { type: 'linear', stops: ['#FF4B4B', '#FFFF27'] } // 主渐变色
const cardColor: IGradientPaint = { type: 'linear', stops: ['#FF4B4B', '#FE9027'] } // 卡片渐变色

const beginButtonText = new Text({
    x: 10,
    y: height / 2 - 30,
    width,
    fontSize: 36,
    fontWeight: 'bold',
    textAlign: 'center',
    italic: true,
    origin: 'center', // 同 CSS 的 transform-origin 功能一样
    fill: { type: 'linear', stops: ['#FF4B4B', '#FFcc27'] },
    stroke: '#990000',
    strokeWidth: 4,
    animation: { // 动画入场效果
        easing: 'linear',
        style: { text: '开始 "心花怒放" 游戏' },
        duration: 2,
        event: { completed() { beginButtonText.hoverStyle = { fill: color } } } // 动画完成事件
    },
    animationOut: { style: { opacity: 0, scale: 3 } }, // 动画移除效果
    event: {
        tap() {
            beginGame() // 开始游戏
            beginButtonText.remove()
        }
    },
    cursor: 'pointer'
})

leafer.add(beginButtonText)

// 愿望进度条

const wishProgress = new Rect({ x: 5, y: 5, width: 0, height: 50, cornerRadius: 25, fill: { type: 'linear', from: 'top-left', to: 'bottom-right', stops: ['#FF4B4B', '#FE9027'] } })

const wishText = new Text({ x: 20, y: 12, fill: 'white', fontWeight: 'bold', fontSize: 24, text: '心花怒放 ¥' })

const scoreText = new Text({ x: 148, y: 12, fill: 'white', fontWeight: 'bold', fontSize: 24, text: 0 })

const wishProgressBar = new Box({ x: 10, y: height - 70, width: width - 20, height: 60, cornerRadius: 30, fill: 'rgba(0,0,0,0.8)', children: [wishProgress, wishText, scoreText] })

// 发放愿望卡片的容器

const wishCardsGroup = new Group()

// 展示完成游戏的容器

const resultGroup = new Group()


// 第三步：愿望交互操作

let beginTime = 0 // 游戏开始时间
let useTime = 0 // 完成一次心花怒放的游戏时间（秒数）
let completed = false // 是否已经完成游戏

// 开始游戏

function beginGame() {
    let lastTrack = 0

    // 重置数据，可以重玩
    beginTime = Date.now()
    completed = false
    scoreText.text = 0
    wishProgress.width = score = 0

    const timer = setInterval(() => { // 随机发送愿望卡片
        if (completed) return clearInterval(timer)

        const wish = wishList[Math.round((wishList.length - 1) * Math.random())]
        let track = Math.round(wishTracks * Math.random())

        if (lastTrack === track) track < wishTracks ? track++ : track-- // 防止在一个轨道上一直发放
        sendWishCard(wish.id, wish.name, wish.value, lastTrack = track) // 发送愿望卡片
    }, sendInterval)

    leafer.add([wishCardsGroup, wishProgressBar])
}

// 发送愿望卡片

function sendWishCard(id: number, name: string, value: number, track: number) {
    const wishCard = new Box({
        id: id.toString(),
        x: 30 + (track - 1) * 90,
        fill: cardColor,
        cornerRadius: 12,
        animation: { // 卡片降下的动画
            style: { y: height + 500 },
            duration: (height / 500) * 5,
            event: { completed() { wishCard.destroy() } } // 动画完成后自动销毁并移除
        },
        cursor: 'pointer',
        children: [
            new Text({ // 愿望内容（竖排）
                width: 60,
                padding: [30, 20],
                text: name,
                fontSize: 24,
                lineHeight: { type: 'percent', value: 1.2 },
                textAlign: 'center',
                verticalAlign: 'bottom',
                fill: 'black',
            })]
    })

    wishCard.once('tap', () => { // 监听点击卡片事件
        wishCard.hittable = false
        wishCard.set( // 点击卡片之后的动画
            { x: 160, y: height - 50, scale: 0.3, opacity: 0.5 },
            {
                easing: 'back-in',
                duration: 1,
                event: {
                    completed() {
                        // 累计心花怒放值
                        score += value
                        wishCard.remove()
                        scoreText.set({ text: score }, 1)
                        wishProgress.width = (width - 30) * Math.min(1, score / bloomScore)

                        if (score >= bloomScore) gameOver() // 达到数值，完成游戏
                    }
                }
            }
        )
    })

    wishCardsGroup.add(wishCard)
}

// 完成游戏

function gameOver() {
    if (completed) return
    completed = true
    useTime = Math.round((Date.now() - beginTime) / 1000 * 10) / 10
    wishCardsGroup.clear()
    wishCardsGroup.remove()
    wishProgressBar.remove()

    leafer.add(resultGroup)

    bloom() // 心花怒放的效果
}

// 重玩游戏

function restartGame() {
    resultGroup.clear()
    resultGroup.remove()
    beginGame()
}

// 显示游戏成绩

function showResult() {
    const resultText = new Text({
        x: 50,
        y: height - 130,
        width: width - 100,
        fontSize: 30,
        fontWeight: 'bold',
        textAlign: 'center',
        italic: true,
        origin: 'center',
        fill: { type: 'linear', from: 'left', to: 'right', stops: ['#FF4B4B', '#FEB027'] },
        stroke: '#990000',
        strokeWidth: 4,
        animation: {
            easing: 'linear',
            style: { text: `恭喜您在 ${useTime} 秒内完成了一次心花怒放，祝您在新的一年里所愿皆能实现（心花怒放）` },
            duration: 3,
        },
        event: { tap() { restartGame() } }, //  重玩游戏
        cursor: 'pointer'
    })

    resultGroup.add(resultText)
}

// 心花怒放的效果

